import { useState, useEffect } from "react";

let isRequest = true
const Users=() => {
    const [users, setUsers] = useState([])

    
    if(isRequest){
        fetch('https://jsonplaceholder.typicode.com/users')
    .then((result) => {
        return result.json();
    })
    .then((data) => setUsers(data));
    isRequest = false
    }
    
    
    return(
        <div>
            <ul>
                {users.map((user) => (
                    <li>{user.name}</li>
                )                 )}
            </ul>
        </div>
    )
}

export default Users
